<?phpnamespace Tuni\AnnonceBundle\Form;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolverInterface;class AnnonceAdminType extends AbstractType{   public $prixtype = array('Sur devis' => 'Sur devis','à discuter' => 'à discuter');    public function buildForm(FormBuilderInterface $builder, array $options)    {        $builder            ->add('titreAnn','text',array('attr'=> array('class'=>'span10')))            ->add('adresse','text',array('required'=>false,'attr'=> array('class'=>'span10')))            ->add('codePostal','text',array('required'=>false,'attr'=> array('class'=>'span10','pattern'=>'[0-9]*', "title"=>"Chiffres")))            ->add('typeAnnonce', 'entity', array('attr'=> array('class'=>'span10'),               'class' => 'TuniAnnonceBundle:TypeAnnonce',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC');                    },                    'property' => 'type',                     'multiple'  =>FALSE,                     'expanded' => TRUE,                                                         ))                ->add('prixAnn','text',array('required'=>false,'attr'=> array('class'=>'span5','pattern'=>'[0-9.]*', "title"=>"Chiffres")))            ->add('prixNegociable', 'choice', array('required' => false,'attr' => array('class' => 'span22'), 'choices' => $this->prixtype,                    'multiple' => false,                    'expanded' => false,                                                       'empty_value' => '--Selectionnez votre tarif--',                    'empty_data' => NULL                        )                )            ->add('descAnn','textarea',array('required'=>false,'attr'=> array('class'=>'wysihtml5 span12','rows'=>'5')))            ->add('disponibilite','textarea',array('required'=>false,'attr'=> array('class'=>'span12','rows'=>'5','placeholder'=>"Décrire votre disponibilité ")))                        ->add('motCles','text',array('required'=>false,'attr'=> array('class'=>'span10')))                        ->add('publier','checkbox',array('required'=>false,'attr'=> array('class'=>'span12')))            ->add('ville', 'entity', array('attr'=> array('class'=>'span5'),               'class' => 'TuniAnnonceBundle:Ville',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC')->setMaxResults(100);                    },                    'property' => 'nomV',                     'multiple'  =>FALSE,                     'expanded' => FALSE,                                    ))            ->add('devise', 'entity', array('attr'=> array('class'=>'span5'),               'class' => 'TuniAnnonceBundle:Devise',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC');                    },                    'property' => 'nom',                     'multiple'  =>FALSE,                     'expanded' => FALSE,                                    ))                            ->add('sousCat', 'entity', array('attr'=> array('class'=>'span10'),               'class' => 'TuniAnnonceBundle:SousCategorie',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC');                    },                    'property' => 'nomSousCat',                     'multiple'  =>FALSE,                     'expanded' => FALSE,                                    ))                             ->add('membre', 'entity', array('attr'=> array('class'=>'span10'),               'class' => 'TuniAnnonceBundle:Membre',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC');                    },                                         'multiple'  =>FALSE,                     'expanded' => FALSE,                                    ))         ;    }    public function setDefaultOptions(OptionsResolverInterface $resolver)    {        $resolver->setDefaults(array(            'data_class' => 'Tuni\AnnonceBundle\Entity\Annonce'        ));    }    public function getName()    {        return 'tuni_annoncebundle_annoncetype';    }}