<?phpnamespace Tuni\AnnonceBundle\Form;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolverInterface;class AnnonceFrontDType extends AbstractType{   public $prixtype = array('Sur devis' => 'Sur devis', 'à discuter' => 'à discuter');    public function buildForm(FormBuilderInterface $builder, array $options)    {        $builder            ->add('titreAnn','text',array('attr'=> array('class'=>'form-control  input-sm','placeholder'=>"Titre de la demande")))                        ->add('descAnn','textarea',array('required'=>false,'attr'=> array('class'=>'form-control','rows'=>'5','placeholder'=>"Ecrire votre demande ")))            ->add('ville', 'entity', array('attr'=> array('class'=>'form-control  input-sm'),               'class' => 'TuniAnnonceBundle:Ville',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC')->setMaxResults(100);                    },                    'property' => 'nomV',                     'multiple'  =>FALSE,                     'expanded' => FALSE,                                    ))                                        ->add('sousCat', 'entity', array('attr'=> array('class'=>'form-control  input-sm '),               'class' => 'TuniAnnonceBundle:SousCategorie',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC');                    },                    'property' => 'nomSousCat',                     'multiple'  =>FALSE,                     'expanded' => FALSE,                                    ))                                     ;    }    public function setDefaultOptions(OptionsResolverInterface $resolver)    {        $resolver->setDefaults(array(            'data_class' => 'Tuni\AnnonceBundle\Entity\Annonce'        ));    }    public function getName()    {            return 'tuni_annoncebundle_annoncetype';    }}