<?phpnamespace Tuni\AnnonceBundle\Form;use Symfony\Component\Form\AbstractType;use Symfony\Component\Form\FormBuilderInterface;use Symfony\Component\OptionsResolver\OptionsResolverInterface;class AnnonceType extends AbstractType{    public function buildForm(FormBuilderInterface $builder, array $options)    {        $builder            ->add('titreAnn','text',array('attr'=> array('class'=>'span10')))            ->add('adresse','text',array('required'=>false,'attr'=> array('class'=>'span10')))            ->add('disponibilite','textarea',array('required'=>false,'attr'=> array('class'=>'span10','rows'=>'5','placeholder'=>"Décrire votre disponibilité ")))                        ->add('codePostal','text',array('required'=>false,'attr'=> array('class'=>'span10','pattern'=>'[0-9]*', "title"=>"Chiffres")))            ->add('typeAnnonce', 'entity', array('attr'=> array('class'=>'span10'),               'class' => 'TuniAnnonceBundle:TypeAnnonce',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC');                    },                    'property' => 'type',                     'multiple'  =>FALSE,                     'expanded' => TRUE,                                    ))                ->add('prixAnn','text',array('required'=>false,'attr'=> array('class'=>'span5','pattern'=>'[0-9.]*', "title"=>"Chiffres")))            ->add('prixNegociable','checkbox',array('required'=>false,'attr'=> array('class'=>'span12')))                        ->add('descAnn','textarea',array('required'=>false,'attr'=> array('class'=>'wysihtml5 span12','rows'=>'5')))            ->add('motCles','text',array('required'=>false,'attr'=> array('class'=>'span10')))                        ->add('publier','checkbox',array('required'=>false,'attr'=> array('class'=>'span12')))            ->add('ville', 'entity', array('attr'=> array('class'=>'span4'),               'class' => 'TuniAnnonceBundle:Ville',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC')->setMaxResults(100);                    },                    'property' => 'nomV',                     'multiple'  =>FALSE,                     'expanded' => FALSE,                                    ))            ->add('devise', 'entity', array('attr'=> array('class'=>'span5'),               'class' => 'TuniAnnonceBundle:Devise',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC');                    },                    'property' => 'nom',                     'multiple'  =>FALSE,                     'expanded' => FALSE,                                    ))                            ->add('sousCat', 'entity', array('attr'=> array('class'=>'span10'),               'class' => 'TuniAnnonceBundle:SousCategorie',                    'query_builder' => function($repository) {                        return $repository->createQueryBuilder('p')->orderBy('p.id', 'ASC');                    },                    'property' => 'nomSousCat',                     'multiple'  =>FALSE,                     'expanded' => FALSE,                                    ))                                     ;    }    public function setDefaultOptions(OptionsResolverInterface $resolver)    {        $resolver->setDefaults(array(            'data_class' => 'Tuni\AnnonceBundle\Entity\Annonce'        ));    }    public function getName()    {        return 'tuni_annoncebundle_annoncetype';    }}